mapper.select:
SELECT DISTINCT d.deposit_id, d.financial_company_id, d.name, d.save_month, d.interest_rate, d.max_interest_rate, d.product_code, d.company_code, d.created_at, d.updated_at FROM deposit d LEFT JOIN deposit_spcl_condition dsc ON d.deposit_id = dsc.deposit_id WHERE 1=1
AND dsc.spcl_condition IN
#{condition}
AND d.save_month <= #{remainTime}
ORDER BY d.max_interest_rate DESC, d.interest_rate DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(DISTINCT d.deposit_id) FROM deposit d LEFT JOIN deposit_spcl_condition dsc ON d.deposit_id = dsc.deposit_id WHERE 1=1
AND dsc.spcl_condition IN
#{condition}
AND d.save_month <= #{remainTime}
