mapper.select:
SELECT deposit_saving_id, financial_company_id, type, name, join_way, interest_description, spcl_condition, ctc_note, max_limit, save_month, interest_rate, max_interest_rate, interest_rate_type, reserve_type, created_at, updated_at FROM deposit_saving WHERE 1=1
AND interest_rate >= #{minRate}
AND interest_rate <= #{minRate}
ORDER BY interest_rate DESC
