SELECT DISTINCT s.saving_id, s.financial_company_id, s.name, s.save_month, s.interest_rate, s.max_interest_rate, s.product_code, s.company_code, s.created_at, s.updated_at FROM saving s LEFT JOIN saving_spcl_condition ssc ON s.saving_id = ssc.saving_id WHERE 1=1
AND ssc.spcl_condition IN
#{condition}
AND s.save_month <= #{remainTime}
ORDER BY s.max_interest_rate DESC, s.interest_rate DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(DISTINCT s.saving_id) FROM saving s LEFT JOIN saving_spcl_condition ssc ON s.saving_id = ssc.saving_id WHERE 1=1
AND ssc.spcl_condition IN
#{condition}
AND s.save_month <= #{remainTime}
