mapper.update:
UPDATE
    wallet
SET balance = #{newBalance}
WHERE wallet_id = #{walletId}

UPDATE wallet
SET balance = CASE
                  WHEN member_id = #{senderMemberId} THEN #{senderNewBalance}
                  WHEN member_id = #{receiverMemberId} THEN #{receiverNewBalance}
    END
WHERE member_id IN (#{senderMemberId}, #{receiverMemberId})

mapper.select:
SELECT wallet_id, member_id, balance, password FROM wallet WHERE member_id IN
#{id}
ORDER BY member_id FOR UPDATE
